import "../App.css"
import React from "react";
import { NavLink , Link} from "react-router-dom";
import { useVideo } from "../contexts/VideoContext";
import {allVideos} from "../Database/allVideos";
import Sidebar from "./"
const Home = () => {
  const {
    state: { historyVideos }
  } = useVideo();

  console.log(historyVideos);
  const getFilteredVideos = (historyVideos) => {
    return historyVideos.filter((item) => item.existsInHistory);
  };

  const filteredHistoryVideos = getFilteredVideos(historyVideos);
    const [isDesktop, setDesktop] = React.useState(window.innerWidth > 720);

  const updateMedia = () => {
    setDesktop(window.innerWidth > 620);
  };

  React.useEffect(() => {
    window.addEventListener("resize", updateMedia);
    return () => window.removeEventListener("resize", updateMedia);
  });
    return (
<div className="main_wrapper">
<Sidebar />
      
     <div className="home-wrapper__main">   
       <main className="home-wrapper__main">
      {historyVideos.length === 0 ? (
        <h1 className="empty-history">No videos seen yet!</h1>)
        : (<h3 className="discover_home">Watch History</h3>)
      }
      <div className= "videos-list-showcase designVideoList">
        {filteredHistoryVideos.map((item) => {
          return (
            <Link to={`/video/${item.id}`} className="video-item-link pointer">
              <div
                className="video-item"
                key={item.id}
              >
                <img
                  style={{ width: "250px" }}
                  className="thumbnail-img"
                  src={item.thumbnail}
                  alt="thumbnail"
                />
                <div className="video-description">
                    <img className="avatar-img" src={item.avatar} alt="avatar"/>
                  <h4 className="video-title">{item.videoTitle}</h4>
                  <p className="small-description">{item.channelName}</p>
                  <p className="small-level">Level: {item.level}</p>
                </div>
              </div>
            </Link>
          );
        })}
        
      </div>
      </main>
      <h3 className="discover_home">Discover More</h3>
      <main
        className="videos-list-showcase designVideoList"
      >
        {allVideos.map((item) => {
          return (
            <Link to={`/video/${item.id}`} className="video-item-link pointer">
              <div
                className="video-item"
                key={item.id}
              >
                <img
                  style={{ width: "250px" }}
                  className="thumbnail-img"
                  src={item.thumbnail}
                  alt="thumbnail"
                />
                <div className="video-description">
                    <img className="avatar-img" src={item.avatar} alt="avatar"/>
                  <h4 className="video-title">{item.videoTitle}</h4>
                  <p className="small-description">{item.channelName}</p>
                  <p className="small-level">Level: {item.level}</p>
                </div>
              </div>
            </Link>
          );
        })}
      </main>
     </div>
    

    {isDesktop ? null : (
        <div className="home-wrapper-mobile">
        <ul className="home-wrapper__div__bottom__list">
          <NavLink to="/" className="side-nav-link">
            <li className="pointer-mobile">
              <i className="fas fa-home pointer"></i>
            </li>
          </NavLink>

          <NavLink to="/watchHistory" className="side-nav-link">
            <li className="pointer-mobile">
              <i className="fas fa-history pointer"></i>
            </li>
          </NavLink>

          <NavLink to="/watchLaterVideos" className="side-nav-link">
            <li className="pointer-mobile">
              <i class="fas fa-clock pointer"></i>
            </li>
          </NavLink>

          <NavLink to="/likedVideos" className="side-nav-link">
            <li className="pointer-mobile">
              <i class="fas fa-thumbs-up pointer"></i>
            </li>
          </NavLink>
         
          <NavLink to="/watchHistory" className="side-nav-link">
            <li className="pointer-mobile">
            <i className="fas fa-list pointer"></i>
            </li>
          </NavLink>
        </ul>
        </div>
    )
    }
    </div>
    )
}

export default Home;
