import React from "react";
import {useVideo} from "../contexts/VideoContext";
import { v4 as uuidv4 } from 'uuid';
import addToPlayList from "../utils/addToPlayList"

const UserPlayLists = ({videoDetails , playlist}) => {
    const {
        state: { playlists }
        , dispatch
    } = useVideo();

    return (
        <li>
        <input
          onChange={() =>
            addToPlayList(playlists, dispatch, playlist, videoDetails)
          }
          id={playlist.listId}
          type="checkbox"
        />
      <label htmlFor={playlist.listId}>{playlist.listName}</label>
      </li>
    );
  };

const PlaylistModal = ({setDisplay , display , videoDetails}) => {
const [playListName , setPlayListName] = React.useState("");

const {
    state: { playlists }
  } = useVideo();

 function createNewPlayList(playListName) {
    playlists.push({
        listName: playListName,
        listId: uuidv4(),
        listVideos: []
    })
    setPlayListName(" ");
}
    return (
        <>
<div className="playlistModal-wrapper"
style={{ 
    // position: "absolute" , top: "100px" , left: "100px", 

width: "25rem", height: "10rem" ,display:display , backgroundColor: "white"}}
>
    <div className="playlistModal-header">
        <h2>Add to Playlist</h2>
    </div>
    <div className="playlistModal-body">
        <ul className="checkbox-playlist">
           {playlists.map((playlist) => {
            <UserPlayLists
            playlist={playlist}
            videoDetails={videoDetails}
            key={playlist.listId}
            />
           })}
        </ul>
        <div className="playlistModal-input">
            <input 
            value={playListName}
            onChange={(e) => setPlayListName(e.target.value)}
            type="text"
            placeholder="Add new playlist"
            />
            <button className="playlistModal-Add"
            disabled={playl}
            onClick={() => createNewPlayList(playListName)}
            >Add new playlist</button>
        </div>
    </div>
    <div className="playlistModal-footer">
        <button id="closeId" className="playlistModal-close"
        onClick={() => setDisplay("none")}
        >
            Close
        </button>
        <button id="AddId" className="playlistModal-AddPlaylist"
        onClick={() => setDisplay("none")}
        >
            Add
        </button>
    </div>
</div>
        </>
    )
}

export default PlaylistModal;
